package java_basics;

public class VariablesContinued {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var = -1000000000;
		long v = 10000000000L; 
		short max = 32767;  //maximum number supported
		short min = -32768; //minimum number supported
		byte smallNumber = 127; //max number supported
		byte bigNumber = -128; //minimum number supported
		double decimalVariable = 305.006;
		boolean decision = true; //can only store true and false values
		char letter = 'a'; //stores only single characters
		System.out.println(var);
		System.out.println(v);
		System.out.println(max);
		System.out.println(min);
		System.out.println(smallNumber);
		System.out.println(bigNumber);
		System.out.println(decimalVariable);
		System.out.println(decision);
		System.out.println(letter);
		
	}

}
